import React, { useState } from "react";
import './Home.css';
import Header from "../Header/Header";
import Menu from "./MenuTabs";
import { packageimage } from "../InstamartJson";
import Packages from "./Packages";

function Package() {
  const [Combo, setCombo] = useState({});
  const [isPackage, setIsPackage] = useState(false);

  const packageDetails = (x) => {
    setCombo(x);
    setIsPackage(true);
  }
  return (
    <div>
      <Header
        Packages="package"
      />
      <Menu />
      {
        isPackage ? (
          <Packages
            Combo={Combo}
          />
        ) : (
          <>
            <h4>Packages</h4>
            <div id="grid">
              {packageimage.map((x) => {
                return (
                  <div
                    id="images"
                    onClick={() => packageDetails(x)}
                    role="button"
                    tabIndex={0}
                  >
                    <img id={x.id} src={x.src} alt='a' />
                    <h2>{x.package}</h2>
                    <p>{x.amount}</p>
                    <p>{x.combo}</p>
                  </div>
                )
              })}
            </div>
          </>
        )
      }
    </div>
  )
}
export default Package
