import React, { useRef } from 'react';
import { Button, Form, Input, Label } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css';
import { Formik } from 'formik';
import * as Yup from "yup";
import ReactDatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

function PackageCreation() {
  const logoFileRef = useRef(null);

  const SignupSchema = Yup.object().shape({
    image: Yup.mixed()
      .required(('please_select_a_file'))
      .test('size', ('largeFileSize'), (file) => !file || (file && file.size < 500000))
      .test('fileType', 'Only .png and .jpeg files are allowed', (file) => !file || (file && ['image/png', 'image/jpeg'].includes(file.type))),
    preferredContactMethod: Yup.string().required('Required'),
    dateOfBirth: Yup.date().required('Required'),
  });

  return (
    <div className='app'>
      <div className='signup'>
        <Formik
          initialValues={{
            package_image:'',
            package:'',
            package1_amount:'',
            combo:'',
            expiry_date:'',
            name:'',
            packagecontains_amount:'',
            Brand:'',
            Dimension:'',
          }}
          validationSchema={SignupSchema}
          onSubmit={(values) => {
            console.log(values, 'asjhbsuib==============');
          }}
        >
          {({ values,
            errors,
            touched,
            handleBlur,
            handleChange,
            handleSubmit,
            setFieldValue,
            setFieldTouched,
          }) => {
            const handleFileUpload = (e) => {
              setFieldTouched(e.target.name, true);
              setFieldValue(e.target.name, e.currentTarget.files[0]);
            };
            return (
              <Form onSubmit={handleSubmit}>
                <Form.Field
                  required
                  name='name'
                  control={Input}
                  value={values.name}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='text'
                  label='First Name'
                  placeholder='Enter your first name'
                  error={touched.name && errors.name}
                />
                <Form.Field
                  required
                  name='expiry_date'
                  control={ReactDatePicker}
                  selected={values.expiry_date}
                  onChange={(e) => setFieldValue('expiry_date', e)}
                  label='Expiry Date'
                  placeholderText='Select date of birth'
                  dateFormat='dd/MM/yyyy'
                  error={touched.expiry_date && errors.expiry_date}
                />
                <Form.Field>
                  <div>
                    <Button basic type="button" content={'choose_file'} onClick={() => logoFileRef.current.click()} />
                    <span>
                      {(values.package_image.name || values.package_image) ? values.package_image.name || values.package_image : 'no_file_choosen'}
                    </span>
                    {touched.package_image && errors.package_image && (
                      <Label basic color="red" pointing="left">
                        {errors.package_image}
                      </Label>
                    )}
                    <input
                      name="package_image"
                      type="file"
                      accept="image/png, image/jpeg"
                      ref={logoFileRef}
                      onChange={handleFileUpload}
                      style={{ display: 'none' }}
                    />
                  </div>
                </Form.Field>
                <Form.Field
                  required
                  name='search'
                  control={Input}
                  type='text'
                  value={values.search}
                  onChange={(e) => setFieldValue('search', e.target.value)}
                  label='search'
                  error={touched.search && errors.search}
                />
                <Button type="submit">Submit</Button>
              </Form>
            )
          }}
        </Formik>
      </div>
    </div>
  )
}

export default PackageCreation;
