import React from "react";
import { Form, Input, Button } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css'
import "./login.css"
import { Formik } from "formik";
import axios from 'axios';
import * as Yup from "yup";

const SignupSchema = Yup.object().shape({
  email: Yup.string()
    .email("Invalid email")
    .required("Email is Required"),
  password: Yup.string()
    .matches(/\w*[a-z]\w*/, "Password must have a small letter")
    .matches(/\w*[A-Z]\w*/, "Password must have a capital letter")
    .matches(/\d/, "Password must have a number")
    .matches(/[!@#$%^&*()\-_"=+{}; :,<.>]/, "Password must have a special character")
    .min(8, ({ min }) => `Password must be at least ${min} characters`)
    .required('Password is required'),
});

const saveUserDetails = (val) => {
  // axios.post('http://172.16.0.86:8000/signin/', { ...val })
  //   .then((response) => {
  //     console.log(response.data[0].login_status, 'response------------')
  //     if (response.data[0].login_status === 'Login sucsessfull') {
  //       document.location.href = "/homePage"
  //     }
  //   })
}

function Login() {
  return (
    <div className="login">
      <div className="login-backround">
        <h1>Signin</h1>
        <Formik
          initialValues={{
            email: "",
            password: "",
          }}
          onSubmit={saveUserDetails}
          validationSchema={SignupSchema}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
          }) => (
            <Form className="form"
              onSubmit={handleSubmit}
            >
              <Form.Field
                values={values.email}
                control={Input}
                label="Email"
                icon="user"
                iconPosition="left"
                placeholder="Email..."
                type="email"
                name="email"
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.email && touched.email}
              />
              <Form.Field
                values={values.password}
                control={Input}
                label="Password"
                icon="lock"
                iconPosition="left"
                placeholder="Password..."
                type="password"
                name="password"
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.password && touched.password}
              />
              <br />
              <Button className="loginbutton" type="submit">Signin</Button>
              <a href="/forgotpassword">Forgot Password?</a>
              <a href="/signup">Don't have an account? Sign Up</a>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  )
}
export default Login;
