import React, { useState } from "react";
import './Home.css';
import Header from "../Header/Header";
import { product } from "../InstamartJson";
import ProductCart from "./ProductCart";
import Menu from "./MenuTabs";

function Product() {
  const [Product, setProduct] = useState({});
  const [Cart, setCart] = useState(false);

  const productDetails = (x) => {
    setProduct(x);
    setCart(true);
  }
  return (
    <div>
      <Header
        Products="product"
      />
      <Menu />
      {
        Cart ? (
          <ProductCart
            Product={Product}
            setCart={setCart}
          />
        ) : (
          <>
            <h4>Products</h4>
            <div id="product">
              {product.map((x) => {
                return (
                  <div id="images" onClick={() => { productDetails(x) }}
                    role="button"
                    tabIndex={0}>
                    <img id={x.id} src={x.src} alt='a' />
                    <p>{x.package}</p>
                    <p>{x.amount}</p>
                  </div>
                )
              })}
            </div>
          </>
        )
      }
    </div>
  )
}
export default Product
