import React, { useRef } from 'react';
import { Button, Form, Input, Label } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css';
import { Formik } from 'formik';
import * as Yup from "yup";
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

function PackageCreation() {
  const packageImageRef = useRef(null);
  const packagecontainsImageRef = useRef(null);
  const { state: { EditItem } } = useLocation();

  // const SignupSchema = Yup.object().shape({
  //   package_image: Yup.mixed()
  //     .required('Please select a file')
  //     .test('size', 'Large file size', (file) => !file || (file && file.size < 500000))
  //     .test('fileType', 'Only .png and .jpeg files are allowed', (file) => !file || (file && ['image/png', 'image/jpeg'].includes(file.type))),
  //   expiry_date: Yup.date().required('Required'),
  //   name: Yup.string().required('Required'),
  //   package: Yup.string().required('Required'),
  //   package_amount: Yup.number().required('Required'),
  //   combo: Yup.string().required('Required'),
  //   packagecontains_image: Yup.mixed()
  //     .required('Please select a file')
  //     .test('size', 'Large file size', (file) => !file || (file && file.size < 500000))
  //     .test('fileType', 'Only .png and .jpeg files are allowed', (file) => !file || (file && ['image/png', 'image/jpeg'].includes(file.type))),
  //   packagecontains_amount: Yup.number().required('Required'),
  //   brand: Yup.string().required('Required'),
  //   Dimension: Yup.string().required('Required'),
  // });

  const packageDetails = (val) => {
    const formData = new FormData();
    if (EditItem.id) {
      formData.append('id', EditItem.id);
      formData.append('name', val.name);
      formData.append('uploadA', val.packagecontains_image);
      formData.append('packagecontains_amount', val.packagecontains_amount);
      formData.append('brand', val.brand);
      formData.append('Dimension', val.Dimension);
    } else {
      formData.append('upload', val.package_image);
      formData.append('package', val.package);
      formData.append('package_amount', val.package_amount);
      formData.append('combo', val.combo);
      formData.append('expiry_date', new Date(val.expiry_date).toISOString());
      formData.append('name', val.name);
      formData.append('uploadA', val.packagecontains_image);
      formData.append('packagecontains_amount', val.packagecontains_amount);
      formData.append('brand', val.brand);
      formData.append('Dimension', val.Dimension);
    }
    axios.post('http://127.0.0.1:8000/instamart/packageDetails/', formData)
      .then((response) => {
        console.log(response)
      })
  }

  return (
    <div className='app'>
      <div className='signup'>
        <Formik
          initialValues={{
            package_image: '',
            expiry_date: '',
            name: '',
            package: '',
            package_amount: '',
            combo: '',
            packagecontains_image: '',
            packagecontains_amount: '',
            brand: '',
            Dimension: '',
          }}
          // validationSchema={SignupSchema}
          onSubmit={packageDetails}
        >
          {({ values,
            errors,
            touched,
            handleBlur,
            handleChange,
            handleSubmit,
            setFieldValue,
            setFieldTouched,
          }) => {
            const handleFileUpload = (e) => {
              setFieldTouched(e.target.name, true);
              setFieldValue(e.target.name, e.currentTarget.files[0]);
            };
            return (
              <Form onSubmit={handleSubmit}>
                <Form.Field
                  required
                  name='name'
                  control={Input}
                  value={values.name}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='text'
                  label='Name'
                  placeholder='Enter name'
                  // error={touched.name && errors.name}
                />
                <Form.Field
                  name='expiry_date'
                  control={DatePicker}
                  selected={values.expiry_date}
                  onChange={(date) => setFieldValue('expiry_date', date)}
                  onBlur={() => setFieldTouched('expiry_date', true)}
                  showTimeSelect
                  timeIntervals={15}
                  dateFormat="MMMM d, yyyy h:mm aa"
                  label="Expiry date"
                  // error={touched.expiry_date && errors.expiry_date}
                />
                <Form.Field>
                  <div>
                    <Button basic type="button" content={'Choose File'} onClick={() => packageImageRef.current.click()} />
                    <span>
                      {values.package_image ? values.package_image.name : 'No file chosen'}
                    </span>
                    {/* {touched.package_image && errors.package_image && (
                      <Label basic color="red" pointing="left">
                        {errors.package_image}
                      </Label>
                    )} */}
                    <input
                      name="package_image"
                      type="file"
                      accept="image/png, image/jpeg"
                      ref={packageImageRef}
                      onChange={handleFileUpload}
                      style={{ display: 'none' }}
                    />
                  </div>
                </Form.Field>
                <Form.Field
                  required
                  name='package'
                  control={Input}
                  value={values.package}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='text'
                  label='Package'
                  placeholder='Enter package'
                  // error={touched.package && errors.package}
                />
                <Form.Field
                  required
                  name='package_amount'
                  control={Input}
                  value={values.package_amount}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='number'
                  label='Package 1 Amount'
                  placeholder='Enter amount'
                  // error={touched.package_amount && errors.package_amount}
                />
                <Form.Field
                  required
                  name='combo'
                  control={Input}
                  value={values.combo}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='text'
                  label='Combo'
                  placeholder='Enter combo'
                  // error={touched.combo && errors.combo}
                />
                <Form.Field>
                  <div>
                    <Button basic type="button" content={'Choose File'} onClick={() => packagecontainsImageRef.current.click()} />
                    <span>
                      {values.packagecontains_image ? values.packagecontains_image.name : 'No file chosen'}
                    </span>
                    {/* {touched.packagecontains_image && errors.packagecontains_image && (
                      <Label basic color="red" pointing="left">
                        {errors.packagecontains_image}
                      </Label>
                    )} */}
                    <input
                      name="packagecontains_image"
                      type="file"
                      accept="image/png, image/jpeg"
                      ref={packagecontainsImageRef}
                      onChange={handleFileUpload}
                      style={{ display: 'none' }}
                    />
                  </div>
                </Form.Field>
                <Form.Field
                  required
                  name='packagecontains_amount'
                  control={Input}
                  value={values.packagecontains_amount}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='number'
                  label='Package Contains Amount'
                  placeholder='Enter amount'
                  // error={touched.packagecontains_amount && errors.packagecontains_amount}
                />
                <Form.Field
                  required
                  name='brand'
                  control={Input}
                  value={values.brand}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='text'
                  label='Brand'
                  placeholder='Enter brand'
                  // error={touched.brand && errors.brand}
                />
                <Form.Field
                  required
                  name='Dimension'
                  control={Input}
                  value={values.Dimension}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  type='text'
                  label='Dimension'
                  placeholder='Enter dimension'
                  // error={touched.Dimension && errors.Dimension}
                />
                <Button type="submit">Submit</Button>
              </Form>
            )
          }}
        </Formik>
      </div>
    </div>
  )
}

export default PackageCreation;
