import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";

function Packages(props) {
  const { Combo, onAddToCart, obj } = props;
  const { packagecontains } = Combo;
  const navigate = useNavigate();
  const [isAddtoCard, setIsAddtoCard] = useState(false)

  const addCart = (id) => {
    // navigate(`/home/myrental/${id}`);
    setIsAddtoCard(true);
  };

  return (
    <>
      {
        isAddtoCard ? (
          <>HIZBCVDZVHB</>
        ) : (
          <div>
            <div id="combo1">
              <div>
                <img src={Combo.src} alt="a" />
              </div>
              <div id="details">
                <h3>{Combo.package}</h3>
                <p>{Combo.amount}</p>
                <p>{Combo.combo}</p>
              </div>
              <div>
                <button id="cart_btn" onClick={() => addCart(Combo.id)}>
                  Add To Cart
                </button>
              </div>
            </div>
            {packagecontains && (
              <>
                <hr></hr>
                <h5 align="center">PACKAGE CONTAINS</h5>
                {packagecontains.map((x, index) => (
                  <div id="combo" key={index}>
                    <div>
                      <img src={x.src} alt="a" />
                    </div>
                    <div id="details">
                      <h3>{x.name}</h3>
                      <p>{x.amount}</p>
                      <p>
                        <b>Brand</b>: {x.Brand}
                      </p>
                      <p>
                        <b>Dimension</b>: {x.Dimension}
                      </p>
                    </div>
                  </div>
                ))}
              </>
            )}
          </div>
        )
      }
    </>
  );
}
export default Packages;
