import React, { useState } from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { useNavigate } from "react-router-dom";
import "./login.css"


const SigninSchema = Yup.object().shape({
  username: Yup.string().required("Username is required."),
  password: Yup.string().required("Password is required."),
});

function Signin() {
  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleSignIn = async (values) => {
    navigate("/home");
    // const { username, password } = values;

    // try {
    //   const response = await fetch("http://localhost:3000/api/userCheck", {
    //     method: "GET",
    //     headers: {
    //       "Content-Type": "application/json",
    //     },
    //     body: JSON.stringify({ username, password }),
    //   });

    //   if (response.ok) {
    //     navigate("/home");
    //   } else {
    //     setError("Username and password are incorrect.");
    //   }
    // } catch (error) {
    //   console.error("Error authenticating:", error);
    // }
  };

  return (
    <div className="signin">
      <Formik
        initialValues={{
          username: "",
          password: "",
        }}
        validationSchema={SigninSchema}
        onSubmit={handleSignIn}
      >
        {({
          errors,
          touched,
          handleSubmit
        }) => (
          <Form id="signin" action="userCheck" method="GET">
            <img src={require("../assets/Images/logo.png")} alt="Logo" />
            <p id="title">Welcome back Instamaster!</p>

            <Field
              type="text"
              id="username"
              placeholder="Username"
              name="username"
            />
            {touched.username && errors.username && (
              <span className="error">{errors.username}</span>
            )}

            <Field
              type="password"
              id="password"
              placeholder="Password"
              name="password"
            />
            {touched.password && errors.password && (
              <span className="error">{errors.password}</span>
            )}

            {error && <p id="error-message">{error}</p>}

            <button id="btn" type="submit">
              SIGN IN
            </button>
            <p>
              New to INSTAMART? <a href="/signup">Sign up</a> instead
            </p>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default Signin;
